SparcRails::Application.configure do
  # Settings specified here will take precedence over those in config/application.rb

  # In the development environment your application's code is reloaded on
  # every request. This slows down response time but is perfect for development
  # since you don't have to restart the web server when you make code changes.
  config.cache_classes = true

  # Eager load code on boot. This eager loads most of Rails and
  # your application in memory, allowing both threaded web servers
  # and those relying on copy on write to perform better.
  # Rake tasks automatically ignore this option for performance.
  config.eager_load = true
  config.enable_dependency_loading = false

  # Show full error reports and disable caching
  config.consider_all_requests_local       = true
  config.action_controller.perform_caching = false

  # Don't care if the mailer can't send
  config.action_mailer.raise_delivery_errors = false

  # Print deprecation notices to the Rails logger
  config.active_support.deprecation = :log

  # Configure the asset pipeline
  config.assets.debug = false

  config.action_mailer.default_url_options = {
    host: ENV['SPARC_MAILER_HOST'],
    script_name: Rails.application.config.relative_url_root
  }
  config.action_mailer.delivery_method = :smtp
  config.action_mailer.smtp_settings = {
    address: ENV['SPARC_MAILER_SMTP_ADDRESS'],
    port: 25,
    enable_starttls_auto: false
  }
  config.action_mailer.perform_deliveries = true

  # See everything in the log (default is :info)
  config.log_level = :debug

  # Enable locale fallbacks for I18n (makes lookups for any locale fall back to
  # the I18n.default_locale when a translation can not be found)
  config.i18n.fallbacks = true

  # Send deprecation notices to registered listeners
  config.active_support.deprecation = :notify

  # Stuff to do on each request
  config.to_prepare do
    DeviseFilters.add_filters
  end

  Paperclip.options[:command_path] = "/usr/bin/"
end
